class HttpServiceAwareTraitTest extends \PHPUnit_Framework_TestCase
{
    /**
     * Test setter
     *
     * @return void
     */
    public function testSetHttpService()
    {
        $mock = $this->getMock('VuFindHttp\HttpService');
        $test = new HttpServiceAwareTraitTestImplementation();
        $test->setHttpService($mock);
        $this->assertEquals($mock, $this->getProperty($test, 'httpService'));
    }

    /**
     * Return protected or private property.
     *
     * Uses PHP's reflection API in order to modify property accessibility.
     *
     * @param object|string $object   Object or class name
     * @param string        $property Property name
     *
     * @throws \ReflectionException Property does not exist
     *
     * @return mixed
     */
    protected function getProperty($object, $property)
    {
        $reflectionProperty = new \ReflectionProperty($object, $property);
        $reflectionProperty->setAccessible(true);
        return $reflectionProperty->getValue($object);
    }
}

class HttpServiceAwareTraitTestImplementation
{
    use \VuFindHttp\HttpServiceAwareTrait;
}